import React, { useState } from 'react';
import { Form, Button, Card, Row } from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import { useNavigate } from 'react-router-dom';
// Components
import FormContainer from '../../components/common/FormContainer';
import BookingSteps from '../../components/booking/BookingSteps';
import Loader from '../../components/common/Loader';
import Message from '../../components/common/Message';
// Slices
import { useGetBarbersQuery } from '../../slices/usersApiSlice';
// import { saveShippingAddress } from '../../slices/cartSlice';
import { saveBarber } from '../../slices/bookingSlice';

function ChooseBarberScreen() {
    // Api Slice: Get the barbers
    const { data: barbers, isLoading, error} = useGetBarbersQuery();
    console.log(barbers)
    // Get the barber global state
    // const booking = useSelector((state) => state.booking);
    // const { barber } = booking;

    // Populate the component local state based on the results of the global state if any
    // const [address, setAddress] = useState(shippingAddress?.address || '');
    // const [city, setCity] = useState(shippingAddress?.city || '');
    // const [postalCode, setPostalCode] = useState(shippingAddress?.postalCode || '');
    // const [country, setCountry] = useState(shippingAddress?.country || '');

    // const [barberName, setBarberName] = useState(barber?.username || '')
    const [selectedBarber, setSelectedBarber] = useState(null);

    // const isFormFilled = address && city && postalCode && country;

    // Initialization
    const navigate = useNavigate();
    const dispatch = useDispatch();

    const submitHandler = (e) => {
        e.preventDefault();
        // Update the global state
        dispatch(saveBarber({selectedBarber}));
        // navigate('/booking-datetime');
    }

      const handleBarberSelect = (barberId) => {
        setSelectedBarber(barberId);
      };

  return (
<>
{ isLoading ? (
      <Loader/>
    ) : error ? (
      <Message variant='danger'>{ error?.data?.message || error.error }</Message>
    ) : (
    <>
    {/* <FormContainer> */}
    <Row className="justify-content-center">
        {/* <BookingSteps step1 /> */}
        <h1>Select Barber</h1>
        <Form onSubmit={submitHandler}>
            {/* Barber */}
            {/* <div className="row"> */}
            <Form.Group controlId='barber' className='my-2 d-flex justify-content-center'>
                <div className="row justify-content-center">
                    { barbers && barbers.map((barber) => (
                        <div key={barber.id} className="col-md-6 ">
                            <Card
                            onClick={() => handleBarberSelect(barber.id)}
                            className={selectedBarber === barber.id ? 'selected-card my-3 p-3 rounded' : 'my-3 p-3 rounded'}
                            >
                                <Card.Img src={barber.image} variant='top' />
                                <Card.Body>
                                    <Card.Title as='div' className='card-title'><strong>{barber.username}</strong></Card.Title>
                                    <Form.Check
                                        type="radio"
                                        id={`barber-${barber.id}`}
                                        label=''
                                        checked={selectedBarber === barber.id}
                                        onChange={() => handleBarberSelect(barber.id)}
                                    />
                                </Card.Body>
                            </Card>
                        </div>
                ))}
                </div>
            </Form.Group>

            {/* Submit Button */}
            <Button
                type='submit'
                variant='primary'
                className='my-2'
                // disabled={!isFormFilled}
            >
                Continue
            </Button>
        </Form>
    {/* </FormContainer> */}
    </Row>
    </>
        ) }
</>
  )
}

export default ChooseBarberScreen
