import React, {useEffect, useState} from 'react'
import { Row, Col, Image } from 'react-bootstrap';
import { useParams, Link } from 'react-router-dom';
// Components
import Product from '../../components/productscreen/Product';
import Loader from '../../components/common/Loader';
import Message from '../../components/common/Message';
import Paginate from '../../components/productscreen/Paginate';
// Slices
import { useGetHaircutsQuery } from '../../slices/haircutsApiSlice';
// Images
import allHatsScreenImage from '../../assets/allHatsScreenImage.jpg';


const HaircutsScreen = () => {
  const { pageNumber } = useParams();

  // Query the haircuts using the useGetHaircutsQuery instead of using axios or fetch
  // const { data, isLoading, error} = useGetHaircutsQuery({pageNumber});
  console.log(data);
// const data = {
//     products: [
//       {
//         _id: '1',
//         user: 'Mitsos',
//         name: 'Fedora Style 1',
//         image: '/images/FedoraStyle1.jpg',
//         brand: 'Stetson',
//         category: 'fedora',
//         description: 'The epitome of elegance and craftsmanship',
//         rating: 0,
//         numReviews: 0,
//         price: 39.99,
//         countInStock: 10
//       },
//       {
//         _id: '2',
//         user: 'Kotsos',
//         name: 'Fedora Style 1',
//         image: '/images/FedoraStyle1.jpg',
//         brand: 'Stetson',
//         category: 'fedora',
//         description: 'The epitome of elegance and craftsmanship',
//         rating: 0,
//         numReviews: 0,
//         price: 39.99,
//         countInStock: 10
//       }
//     ]
//   };
  const isLoading = false;
  const error = false;
  const [haircuts, setHaircuts] = useState([]);

  useEffect(() => {
    fetch('http://localhost:8000/api/haircuts')
      .then(response => {
        if (!response.ok) {
          throw new Error('Network response was not ok');
        }
        return response.json();
      })
      .then(data => {
        setHaircuts(data);
      })
      .catch(error => {
        console.error('There has been a problem with your fetch operation:', error);
      });
  }, []); // The empty array means this effect runs once on mount

  return (
    <>
      {/* Back nav button after search */}
        ( <Link to='/haircuts' className='btn btn-light mb-4'>Go Back</Link> )

      { isLoading ? (
        <Loader/>
      ) : error ? (
        <Message variant='danger'>{ error?.data?.message || error.error }</Message>
      ) : (
        <>
          <Row>
              <Image src={allHatsScreenImage} alt='hats image' fluid style={{height: '400px', width: '100%', objectFit: 'cover'}} className='mb-4'/>
          </Row>
          <h1>All Hats</h1>
          <Row>
              {data.map((product) => (
                  <Col key={product._id} sm={12} md={6} lg={4} xl={3}>
                      {/* <Product product={product} category='all' /> */}
                  </Col>
              ))}
          </Row>
          <Paginate
            pages={data.pages}
            page={data.page}
          />
        </>
      ) }


    </>
  )
}

export default HaircutsScreen;
